    val listData: ArrayList<GameData>
    get() {
        val list = arrayListOf<GameData>()
        for (position in gameName.indices){
            val game = GameData()
            game.name = gameName[position]
            game.producer = producer[position]
            game.rating = rating[position]
            game.details = details[position]
            game.photo = gamePhotos[position]
            game.logo = gameLogo[position]
            list.add(game)
        }
        return list
    }

